import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState('');
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dueDate, setDueDate] = useState('');

  const API_BASE_URL = 'http://localhost:5000/api'; // Directly using the backend URL

  // Memoized fetch function with proper error handling
  const fetchTodos = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const { data } = await axios.get(`${API_BASE_URL}/todos`);
      // Format dates for display
      const formattedTodos = data.map(todo => ({
        ...todo,
        createdAt: todo.createdAt ? new Date(todo.createdAt).toLocaleDateString() : '',
        dueDate: todo.dueDate ? new Date(todo.dueDate).toLocaleDateString() : ''
      }));
      setTodos(formattedTodos);
    } catch (err) {
      setError(err.message || 'Failed to fetch todos');
      console.error('Error fetching todos:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchTodos();
  }, [fetchTodos]);

  const addTodo = async () => {
    if (!task.trim()) return;

    try {
      const { data } = await axios.post(`${API_BASE_URL}/todos`, {
        task: task.trim(),
        completed: false,
        dueDate: dueDate || undefined // Send date only if provided
      });

      // Format the new todo's dates
      const formattedTodo = {
        ...data,
        createdAt: data.createdAt ? new Date(data.createdAt).toLocaleDateString() : '',
        dueDate: data.dueDate ? new Date(data.dueDate).toLocaleDateString() : ''
      };

      setTodos([...todos, formattedTodo]);
      setTask('');
      setDueDate('');
    } catch (err) {
      setError(err.message || 'Failed to add todo');
      console.error('Error adding todo:', err);
    }
  };

  const toggleTodo = async (id) => {
    try {
      const todo = todos.find(t => t._id === id);
      const { data } = await axios.patch(`${API_BASE_URL}/todos/${id}`, {
        completed: !todo.completed
      });
      setTodos(todos.map(t => t._id === id ? {
        ...data,
        createdAt: data.createdAt ? new Date(data.createdAt).toLocaleDateString() : '',
        dueDate: data.dueDate ? new Date(data.dueDate).toLocaleDateString() : ''
      } : t));
    } catch (err) {
      setError(err.message || 'Failed to update todo');
      console.error('Error toggling todo:', err);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/todos/${id}`);
      setTodos(todos.filter(t => t._id !== id));
    } catch (err) {
      setError(err.message || 'Failed to delete todo');
      console.error('Error deleting todo:', err);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'completed') return todo.completed;
    if (filter === 'pending') return !todo.completed;
    return true;
  });

  return (
    <div className="app">
      <h1>Todo App</h1>

      {error && <div className="error-message">{error}</div>}

      <div className="add-todo">
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Task description"
          onKeyPress={(e) => e.key === 'Enter' && addTodo()}
          disabled={loading}
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          placeholder="Due date (optional)"
        />
        <button onClick={addTodo} disabled={loading || !task.trim()}>
          {loading ? 'Adding...' : 'Add Todo'}
        </button>
      </div>

      <div className="filters">
        <button onClick={() => setFilter('all')} className={filter === 'all' ? 'active' : ''}>
          All
        </button>
        <button onClick={() => setFilter('completed')} className={filter === 'completed' ? 'active' : ''}>
          Completed
        </button>
        <button onClick={() => setFilter('pending')} className={filter === 'pending' ? 'active' : ''}>
          Pending
        </button>
      </div>

      {loading ? (
        <div className="loading">Loading todos...</div>
      ) : (
        <ul className="todo-list">
          {filteredTodos.map(todo => (
            <li key={todo._id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
              <div className="todo-content">
                <span onClick={() => toggleTodo(todo._id)}>
                  {todo.task}
                </span>
                <div className="todo-dates">
                  {todo.createdAt && <span>Created: {todo.createdAt}</span>}
                  {todo.dueDate && <span>Due: {todo.dueDate}</span>}
                </div>
              </div>
              <button 
                onClick={() => deleteTodo(todo._id)}
                disabled={loading}
                className="delete-btn"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
